<?php

namespace App\Http\Controllers;

use App\Models\Visite;
use Dompdf\Dompdf;
use Dompdf\Options;
use Illuminate\Http\Request;
use NumberFormatter;

class PdfController extends Controller
{

    public function devis_rapide_pdf(Request $request){

        $base_64_image_garage = "data:image/jpeg;base64,";

        $df = $request->all();

        $nom_client = $df['nom_client'];
        $marque = $df['marque'];
        $model = $df['model'];
        $immatriculation = $df['immatriculation'];

        $date_facture = $df['date_facture'];
        $grand_total = $df['grand_total'];
        $main_doeuvre = $df['main_doeuvre'];
        $total_a_payer = $df['total_a_payer'];


        $convertir_en_string = new NumberFormatter("fr", NumberFormatter::SPELLOUT);
        $total_a_payer_en_lettre = $convertir_en_string->format($total_a_payer);

        $liste_objet = [];
        $nombre_objet = sizeof($df['objet']);
        for ($i=0;$i<$nombre_objet;$i++){
            $objet = $df['objet'][$i];
            if(!empty($objet)){
                $prix_unitaire = $df['prix_unitaire'][$i];
                $quantite_pour_facture = $df['quantite_pour_facture'][$i];
                $prix_total = $df['prix_total'][$i];

                $objet = [
                    'objet' => $objet,
                    'prix_unitaire' => $prix_unitaire,
                    'quantite_pour_facture' => $quantite_pour_facture,
                    'prix_total' => $prix_total,
                ];

                array_push($liste_objet,$objet);
            }
        }
        $version_objet = false;

//        dd($liste_objet);

        $options = new Options();
        $options->set('isRemoteEnabled',true);
        $options->set('debugKeepTemp', TRUE);
        $options->set('isHtml5ParserEnabled', true);


        $domPdf = new Dompdf($options);
//        $domPdf->loadHtmlFile('pdf.devis_rapide_pdf');
        $domPdf->loadHtml(view('pdf.devis_rapide_pdf',
                              compact('liste_objet','nom_client','marque','model','immatriculation',
                                  'date_facture','grand_total','main_doeuvre','total_a_payer','base_64_image_garage','version_objet','total_a_payer_en_lettre'))
        );

        $domPdf->setPaper('A4');
//        $domPdf->setPaper('A4','landscape');

        //convertir en pdf
        $domPdf->render();

        //afficher le pdf
        $nom = $date_facture.'-'.$immatriculation.'.pdf';
        $domPdf->stream($nom,['Attachment'=>false]);
    }

